type views = 'home' | 'full';
const toggleView:{[index:string]:views} = {
    'full':'home',
    'home':'full',
};

export function Toggle({setState, current}) {
    return (
        <React.Fragment>
            <input hidden onClick={()=>setState(toggleView[current])} data-testid='toggler-input-hidden' />
            <Toggler
                defaultChecked={current=='full'}
                icons={{
                    checked: listViewIcon,
                    unchecked: compactViewIcon,
                }}
                onChange={()=>{
                    Cookie.set('post_view', toggleView[current], { expires: 7 });
                    setState(toggleView[current]);
                }}
            />
        </React.Fragment>
    );
}


/**
 * small red dot, displayed beneath notification icon
 * whenever thers a new notification
 *
 */
export function AlertIndicator() {
    const {newNotification} = useSelector<Root,Root>(s => s);

    return (
        <React.Fragment>
            {newNotification && <span id='red-dot'></span>}
        </React.Fragment>
    );
}


/**
 * input component for uploading user DP
 */
export const UploadUserDP: React.FC<{
    inputRef: React.RefObject<HTMLInputElement>,
    mutate: Function
}> = ({inputRef, mutate})=>{
    const dispatch = useDispatch();

    const uploadDP = async (ev)=>{
        let res = await uploadUserDP(ev);

        if (res?.errors) {
            showAlert(dispatch, res.errors);
        } else if (res?.success) {
            showAlert(dispatch, ['Done! it may take a while for changes to reflect'], 'success');
        }
    }

    return (
        <React.Fragment>
            <input hidden accept='image/*' name="image" type='file' ref={inputRef} onChange={uploadDP} />
        </React.Fragment>
    );
}
